mapper.select:
 select stock_user.*,
ifnull((select sum(charge_total_money) from  charging_record  cr where
 cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_total_money,
 ifnull((select sum(charge_num) from  charging_record  cr where
 cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_num,
 ifnull((select count(id) from  charging_record  cr where
 cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_count
 from stock_user


       and  tel=#{tel}


       and  email=#{email}


       and   user_uid=#{userUid}


       and  id=#{id}


       and  user_type = #{userType}


     order by id desc

select * from ( select stock_user.id,stock_user.user_uid, stock_user.create_time,stock_user.agent_user_id, stock_user.user_type, agent_user.account as agent_account , agent_user.username as agent_username,
stock_user_capital_fund.usable_fund ,stock_user_capital_fund.card_num, ifnull((select sum(charge_total_money) from charging_record cr where cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_total_money, ifnull((select sum(charge_num) from charging_record cr where cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_num, ifnull((select count(id) from charging_record cr where cr.stock_user_id=stock_user.id and cr.payment_status=1),0) as charge_count from stock_user left join agent_user on agent_user.id=stock_user.agent_user_id left join stock_user_capital_fund on stock_user_capital_fund.stock_user_id=stock_user.id and stock_user_capital_fund.stock_code = 'ICCARD') as users
and users.user_uid=#{userUid}
and users.agent_user_id = #{agentUserId}
and users.id=#{id}
and users.user_type = #{userType}
and users.agent_account like '${agentAccount}%'
and users.agent_username like '${agentUsername}%'
and users.card_num like '${cardNum}%'
order by users.id desc
